import React, { useState } from "react";
import { useAuth } from "../contexts/AuthContext";
import axios from "axios";

const ProfilePage = () => {
  const { user, setUser } = useAuth();
  const [formData, setFormData] = useState({
    name: user?.name || "",
    email: user?.email || "",
    userId: user?.userId || "",
    password: "",
    specialization: user?.specialization || "",
    experience: user?.experience || "",
    patientsServed: user?.patientsServed || "",
    rating: user?.rating || "",
    location: user?.location || "",
    contact: user?.contact || "",
    availability: user?.availability?.join(", ") || "",
  });
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => setProfilePhoto(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = new FormData();
      Object.keys(formData).forEach((key) => data.append(key, formData[key]));
      if (profilePhoto) data.append("profilePhoto", profilePhoto);

      const res = await axios.put(`/api/users/${user._id}`, data, {
        headers: { Authorization: `Bearer ${user.token}` },
      });

      const updatedUser = res.data.user;
      localStorage.setItem("user", JSON.stringify({ ...updatedUser, token: user.token }));
      setUser({ ...updatedUser, token: user.token });
      alert("Profile updated successfully!");
    } catch (err) {
      alert(err.response?.data?.message || "Update failed");
    } finally {
      setLoading(false);
    }
  };

  const handleDeletePhoto = async () => {
    try {
      await axios.delete(`/api/users/profile-photo/${user._id}`, {
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setProfilePhoto(null);
      setUser({ ...user, profilePhoto: null });
      localStorage.setItem("user", JSON.stringify({ ...user, profilePhoto: null }));
      alert("Profile photo deleted");
    } catch (err) {
      alert(err.response?.data?.message || "Delete failed");
    }
  };

  return (
    <div className="profile-page">
      <h2>My Profile</h2>
      <form onSubmit={handleSubmit} className="profile-form">
        <div>
          <label>Name</label>
          <input name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>UserId</label>
          <input name="userId" value={formData.userId} onChange={handleChange} />
        </div>
        <div>
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} />
        </div>

        {user.role === "doctor" && (
          <>
            <div>
              <label>Specialization</label>
              <input name="specialization" value={formData.specialization} onChange={handleChange} />
            </div>
            <div>
              <label>Experience (years)</label>
              <input name="experience" type="number" value={formData.experience} onChange={handleChange} />
            </div>
            <div>
              <label>Patients Served</label>
              <input name="patientsServed" type="number" value={formData.patientsServed} onChange={handleChange} />
            </div>
            <div>
              <label>Rating</label>
              <input name="rating" type="number" step="0.1" value={formData.rating} onChange={handleChange} />
            </div>
            <div>
              <label>Location</label>
              <input name="location" value={formData.location} onChange={handleChange} />
            </div>
            <div>
              <label>Contact</label>
              <input name="contact" value={formData.contact} onChange={handleChange} />
            </div>
            <div>
              <label>Availability (comma separated)</label>
              <input name="availability" value={formData.availability} onChange={handleChange} />
            </div>
          </>
        )}

        <div>
          <label>Profile Photo</label>
          <input type="file" onChange={handleFileChange} />
          {user.profilePhoto && (
            <div>
              <img src={`/uploads/${user.profilePhoto}`} alt="Profile" width="100" />
              <button type="button" onClick={handleDeletePhoto}>Delete Photo</button>
            </div>
          )}
        </div>

        <button type="submit" disabled={loading}>{loading ? "Updating..." : "Update Profile"}</button>
      </form>
    </div>
  );
};

export default Profile;
