import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Doctors.css';

export default function Doctors() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors');
        setDoctors(res.data);
      } catch (err) {
        console.error(err);
        setError('Failed to load doctors. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctors();
  }, []);

  if (loading) return <p className="loading">Loading doctors...</p>;
  if (error) return <p className="error">{error}</p>;
  if (doctors.length === 0) return <p className="no-data">No doctors available.</p>;

  const handleCardClick = (id) => {
    navigate(`/doctors/${id}`);
  };

  return (
    <div className="doctors-page">
      <h1 className="page-title">Our Doctors</h1>
      <div className="doctors-grid">
        {doctors.map(doc => (
          <div
            key={doc._id}
            className="doctor-card"
            onClick={() => handleCardClick(doc._id)}
          >
            <img
              src={doc.image || '/default-doctor.png'}
              alt={doc.name}
              className="doctor-image"
            />
            <h2 className="doctor-name">{doc.name}</h2>
            <p className="doctor-specialization">{doc.specialization}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
