import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function MyAppointments() {
  const { user, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!token) return;

    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments/patient", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        alert(err?.response?.data?.message || "Failed to load appointments");
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [token]);

  if (loading) return <p>Loading your appointments...</p>;

  return (
    <div>
      <h1>My Appointments</h1>
      {appointments.length === 0 ? (
        <p>No appointments yet</p>
      ) : (
        <ul>
          {appointments.map((a) => (
            <li key={a._id}>
              {a.doctorName} ({a.doctorEmail}) - {a.date} {a.time} - Status: {a.status}
              {a.meetLink && <a href={a.meetLink}>Join Meeting</a>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
