import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({});
  const [imageFile, setImageFile] = useState(null);
  const [msg, setMsg] = useState('');

  const token = localStorage.getItem('token');

  // Fetch current user from MongoDB
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/users/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(res.data);
        setForm(res.data);
      } catch (err) {
        setMsg(err.response?.data?.message || 'Failed to load profile');
      }
    };
    fetchProfile();
  }, [token]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const handleImageChange = (e) => setImageFile(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();

    // Append all form fields
    Object.keys(form).forEach((key) => {
      if (Array.isArray(form[key])) {
        data.append(key, form[key].join(','));
      } else if (form[key] !== undefined) {
        data.append(key, form[key]);
      }
    });

    if (imageFile) data.append('profilePhoto', imageFile);

    try {
      const res = await axios.put('http://localhost:5000/api/users/me', data, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' },
      });
      setProfile(res.data);
      setForm(res.data);
      setEditMode(false);
      setMsg('Profile updated successfully!');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Update failed');
    }
  };

  if (!profile) return <p>Loading...</p>;

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <h1>My Profile</h1>

      {profile.profilePhoto && (
        <img
          src={profile.profilePhoto.startsWith('/uploads') ? `http://localhost:5000${profile.profilePhoto}` : profile.profilePhoto}
          alt={profile.name}
          style={{ width: '150px', borderRadius: '50%' }}
        />
      )}

      {!editMode ? (
        <div>
          <p><strong>Name:</strong> {profile.name}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Role:</strong> {profile.role}</p>

          {profile.role === 'doctor' && (
            <>
              <p><strong>Specialization:</strong> {profile.specialization || 'N/A'}</p>
              <p><strong>Experience:</strong> {profile.experience || 'N/A'}</p>
              <p><strong>Availability:</strong> {profile.availability?.join(', ') || 'N/A'}</p>
              <p><strong>Patients Served:</strong> {profile.patientsServed || 'N/A'}</p>
              <p><strong>Rating:</strong> {profile.rating || 'N/A'}</p>
              <p><strong>Location:</strong> {profile.location || 'N/A'}</p>
              <p><strong>Contact:</strong> {profile.contact || 'N/A'}</p>
            </>
          )}

          {profile.role === 'patient' && (
            <>
              <p><strong>Age:</strong> {profile.age || 'N/A'}</p>
              <p><strong>Medical History:</strong> {profile.medicalHistory?.join(', ') || 'N/A'}</p>
            </>
          )}

          {profile.role === 'pharmacist' && (
            <>
              <p><strong>Pharmacy Name:</strong> {profile.pharmacyName || 'N/A'}</p>
              <p><strong>Location:</strong> {profile.location || 'N/A'}</p>
              <p><strong>Available Medicines:</strong> {profile.medicines?.join(', ') || 'N/A'}</p>
            </>
          )}

          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <input name="name" value={form.name || ''} onChange={handleChange} placeholder="Name" required />
          <input name="email" value={form.email || ''} onChange={handleChange} placeholder="Email" type="email" required />
          <input type="file" accept="image/*" onChange={handleImageChange} />

          {profile.role === 'doctor' && (
            <>
              <input name="specialization" value={form.specialization || ''} onChange={handleChange} placeholder="Specialization" />
              <input name="experience" value={form.experience || ''} onChange={handleChange} placeholder="Experience" type="number" />
              <input name="availability" value={form.availability?.join(', ') || ''} onChange={e => setForm({ ...form, availability: e.target.value.split(',').map(s => s.trim()) })} placeholder="Availability (comma separated)" />
              <input name="location" value={form.location || ''} onChange={handleChange} placeholder="Location" />
              <input name="contact" value={form.contact || ''} onChange={handleChange} placeholder="Contact" />
            </>
          )}

          {profile.role === 'patient' && (
            <>
              <input name="age" value={form.age || ''} onChange={handleChange} placeholder="Age" type="number" />
              <textarea
                name="medicalHistory"
                value={form.medicalHistory?.join(', ') || ''}
                onChange={e => setForm({ ...form, medicalHistory: e.target.value.split(',').map(s => s.trim()) })}
                placeholder="Medical History (comma separated)"
              />
            </>
          )}

          {profile.role === 'pharmacist' && (
            <>
              <input name="pharmacyName" value={form.pharmacyName || ''} onChange={handleChange} placeholder="Pharmacy Name" />
              <input name="location" value={form.location || ''} onChange={handleChange} placeholder="Location" />
              <textarea
                name="medicines"
                value={form.medicines?.join(', ') || ''}
                onChange={e => setForm({ ...form, medicines: e.target.value.split(',').map(s => s.trim()) })}
                placeholder="Available Medicines (comma separated)"
              />
            </>
          )}

          <button type="submit">Save Changes</button>
          <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
          {msg && <p style={{ color: msg.includes('success') ? 'green' : 'red' }}>{msg}</p>}
        </form>
      )}
    </div>
  );
}
