// frontend/src/pages/DoctorAppointments.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function DoctorAppointments() {
  const { currentUser } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments", {
          headers: { Authorization: `Bearer ${currentUser.token}` },
        });
        // Filter only appointments for logged-in doctor
        setAppointments(res.data.filter(a => a.doctorId === currentUser.userId));
      } catch (err) {
        console.error(err);
        setError("Failed to load appointments");
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
  }, [currentUser]);

  const handleAction = async (appointmentId, status) => {
    setActionLoading(true);
    try {
      await axios.put(
        `http://localhost:5000/api/appointments/respond/${appointmentId}`,
        { status },
        { headers: { Authorization: `Bearer ${currentUser.token}` } }
      );
      setAppointments(prev =>
        prev.map(a =>
          a._id === appointmentId ? { ...a, status, meetLink: status === "approved" ? a.meetLink : "" } : a
        )
      );
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "Failed to update appointment");
    } finally {
      setActionLoading(false);
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointments found.</p>;

  return (
    <div>
      <h1>Appointment Requests</h1>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {appointments.map(a => (
          <li key={a._id} style={{ border: "1px solid #ccc", margin: "10px 0", padding: "10px" }}>
            <p>
              <strong>Patient:</strong> {a.patientName} | <strong>Email:</strong> {a.patientEmail}
            </p>
            <p>
              <strong>Date:</strong> {a.date} | <strong>Time:</strong> {a.time} | <strong>Status:</strong> {a.status}
            </p>
            {a.status === "approved" && (
              <p>
                <strong>Meet Link:</strong>{" "}
                <a href={a.meetLink} target="_blank" rel="noreferrer">
                  {a.meetLink}
                </a>
              </p>
            )}
            {a.status === "pending" && (
              <div style={{ marginTop: "10px" }}>
                <button
                  onClick={() => handleAction(a._id, "approved")}
                  disabled={actionLoading}
                  style={{ marginRight: "10px" }}
                >
                  Approve
                </button>
                <button
                  onClick={() => handleAction(a._id, "rejected")}
                  disabled={actionLoading}
                >
                  Reject
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
