import { Link } from 'react-router-dom';
import './DoctorCard.css';

export default function DoctorCard({ doctor }) {
  let imageUrl = null;

  // Only set imageUrl if doctor.image exists
  if (doctor?.image) {
    // If it starts with http, use as-is
    if (doctor.image.startsWith('http')) {
      imageUrl = doctor.image;
    } 
    // If it starts with /, append backend base URL
    else if (doctor.image.startsWith('/')) {
      imageUrl = `http://localhost:5000${encodeURI(doctor.image)}`;
    } 
    // If just a filename, assume it's in /uploads/
    else {
      imageUrl = `http://localhost:5000/uploads/${encodeURI(doctor.image)}`;
    }
  }

  return (
    <div className="doctor-card">
      {/* Only render img if imageUrl exists */}
      {imageUrl && (
        <img
          src={imageUrl}
          alt={doctor.name || 'Doctor'}
          className="doctor-card-image"
        />
      )}
      <div className="doctor-card-info">
        <h2 className="doctor-card-name">{doctor.name || 'N/A'}</h2>
        {doctor.specialization && <p>Specialization: {doctor.specialization}</p>}
        {doctor.experience !== undefined && <p>Experience: {doctor.experience} yrs</p>}
        {doctor.rating !== undefined && <p>Rating: {doctor.rating}</p>}
        <Link to={`/doctors/${doctor.userId}`} className="doctor-card-link">
          View Details
        </Link>
      </div>
    </div>
  );
}
