import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function DoctorAppointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments/doctor", {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch appointments.");
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [user]);

  const handleStatusChange = async (id, status) => {
    try {
      const res = await axios.patch(
        `http://localhost:5000/api/appointments/${id}`,
        { status },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      setAppointments(prev =>
        prev.map(app => (app._id === id ? res.data.appointment : app))
      );
    } catch (err) {
      console.error(err);
      alert("Failed to update status.");
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  if (!appointments.length) return <p>No appointment requests yet.</p>;

  return (
    <div>
      <h2>Appointment Requests</h2>
      {appointments.map(app => (
        <div key={app._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
          <p><b>Patient:</b> {app.patientName}</p>
          <p><b>Email:</b> {app.patientEmail}</p>
          <p><b>Date:</b> {app.date}</p>
          <p><b>Time:</b> {app.time}</p>
          <p><b>Status:</b> {app.status}</p>
          {app.status === "pending" && (
            <>
              <button onClick={() => handleStatusChange(app._id, "approved")}>Approve</button>
              <button onClick={() => handleStatusChange(app._id, "rejected")}>Reject</button>
            </>
          )}
          {app.meetLink && <p><b>Meeting Link:</b> <a href={app.meetLink} target="_blank">{app.meetLink}</a></p>}
        </div>
      ))}
    </div>
  );
}
