import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

export default function Signup() {
  const [form, setForm] = useState({ name: '', email: '', password: '', role: 'patient' });
  const [msg, setMsg] = useState('');
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/signup', form);
      setMsg(res.data.message);
      setTimeout(() => navigate('/login'), 1500);
    } catch (err) {
      setMsg(err.response?.data?.message || 'Signup failed');
    }
  };

  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1 className="signup-title">Signup</h1>
        <form onSubmit={handleSubmit} className="signup-form">
          <input name="name" type="text" placeholder="Name" value={form.name} onChange={handleChange} required />
          <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
          <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} required />
          <div className="role-selection">
            <label>
              <input type="radio" name="role" value="patient" checked={form.role === 'patient'} onChange={handleChange} /> Patient
            </label>
            <label>
              <input type="radio" name="role" value="doctor" checked={form.role === 'doctor'} onChange={handleChange} /> Doctor
            </label>
          </div>
          {form.role === 'doctor' && <p className="doctor-note">Note: As a doctor, your data will be added to our doctors' list.</p>}
          <button type="submit" className="signup-btn">Signup</button>
        </form>
        {msg && <p className="signup-msg">{msg}</p>}
      </div>
    </div>
  );
}
