import { Link } from 'react-router-dom';
import './DoctorCard.css';

export default function DoctorCard({ doctor }) {
  let imageUrl = null;

  if (doctor?.image) {
    // Use encodeURIComponent to handle spaces
    imageUrl = `http://localhost:5000/uploads/${encodeURIComponent(doctor.image)}`;
  }

  return (
    <div className="doctor-card">
      {/* Render image only if it exists */}
      {imageUrl && (
        <img
          src={imageUrl}
          alt={doctor.name || 'Doctor'}
          className="doctor-card-image"
        />
      )}
      <div className="doctor-card-info">
        <h2 className="doctor-card-name">{doctor.name || 'N/A'}</h2>
        <p><strong>Specialization:</strong> {doctor.specialization || 'N/A'}</p>
        <p><strong>Experience:</strong> {doctor.experience ?? 'N/A'} years</p>
        <p><strong>Patients Served:</strong> {doctor.patientsServed ?? 'N/A'}</p>
        <p><strong>Rating:</strong> {doctor.rating ?? 'N/A'}</p>
        <p><strong>Location:</strong> {doctor.location || 'N/A'}</p>
        <p><strong>Contact:</strong> {doctor.contact || 'N/A'}</p>
        <p>
          <strong>Availability:</strong> {doctor.availability?.length ? doctor.availability.join(', ') : 'N/A'}
        </p>
        <Link to={`/doctors/${doctor.userId}`} className="doctor-card-link">
          View Profile
        </Link>
      </div>
    </div>
  );
}
