// frontend/src/pages/BookAppointment.jsx
import { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./BookAppointment.css";

export default function BookAppointment() {
  const { doctorId } = useParams(); // doctorId from route
  const { user, token } = useAuth(); // logged-in patient
  const navigate = useNavigate();

  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [submitting, setSubmitting] = useState(false);

  // Fetch doctor from dummyDoctorData.json
  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get("http://localhost:5000/data/dummyDoctorData.json");
        const doctors = res.data;
        const selectedDoctor = doctors.find((d) => d.userId === doctorId);
        if (!selectedDoctor) {
          setError("Doctor not found.");
        } else {
          setDoctor(selectedDoctor);
        }
      } catch (err) {
        console.error(err);
        setError("Failed to load doctor details.");
      } finally {
        setLoading(false);
      }
    };

    if (doctorId) fetchDoctor();
  }, [doctorId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!date || !time) {
      alert("Please select date and time.");
      return;
    }

    if (!user || !doctor) {
      alert("Patient or doctor information missing.");
      return;
    }

    setSubmitting(true);

    const appointmentData = {
      patientId: user.userId,
      patientName: user.name,
      patientEmail: user.email,
      doctorId: doctor.userId,
      doctorName: doctor.name,
      doctorEmail: doctor.email,
      date,
      time,
      status: "pending", // default
      meetLink: "", // can be added later by doctor
    };

    try {
      await axios.post("http://localhost:5000/api/appointments", appointmentData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Appointment request sent successfully!");
      navigate("/my-appointments");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "Failed to book appointment.");
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <p>Loading doctor details...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!doctor) return <p>Doctor not found.</p>;

  return (
    <div className="book-appointment-page">
      <Link to={`/doctors/${doctor.userId}`}>← Back to Doctor</Link>
      <h1>Book Appointment with {doctor.name}</h1>

      <form onSubmit={handleSubmit} className="appointment-form">
        <div>
          <label>Patient Name:</label>
          <input type="text" value={user.name} readOnly />
        </div>
        <div>
          <label>Patient Email:</label>
          <input type="email" value={user.email} readOnly />
        </div>
        <div>
          <label>Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={submitting}>
          {submitting ? "Booking..." : "Book Appointment"}
        </button>
      </form>
    </div>
  );
}
