import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({});
  const [imageFile, setImageFile] = useState(null);
  const [msg, setMsg] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/users/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(res.data);
        setForm(res.data); // initialize form fields
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [token]);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleImageChange = e => setImageFile(e.target.files[0]);

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg('');

    const data = new FormData();
    for (const key in form) data.append(key, form[key]);
    if (imageFile) data.append('image', imageFile);

    try {
      const res = await axios.put('http://localhost:5000/api/users/me', data, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setProfile(res.data);
      setForm(res.data);
      setEditMode(false);
      setImageFile(null);
      setMsg('Profile updated successfully!');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Update failed');
    }
  };

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!profile) return <p>No profile found</p>;

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <h1>My Profile</h1>

      {profile.image && (
        <img
          src={`http://localhost:5000${profile.image}`}
          alt={profile.name}
          style={{ width: '150px', borderRadius: '50%' }}
        />
      )}

      {!editMode ? (
        <div>
          <p><strong>Name:</strong> {profile.name}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Role:</strong> {profile.role}</p>

          {profile.role === 'doctor' && (
            <>
              <p><strong>Specialization:</strong> {profile.specialization}</p>
              <p><strong>Experience:</strong> {profile.experience} years</p>
              <p><strong>Availability:</strong> {profile.availability.join(', ')}</p>
              <p><strong>Patients Served:</strong> {profile.patientsServed}</p>
              <p><strong>Rating:</strong> {profile.rating}</p>
              <p><strong>Location:</strong> {profile.location}</p>
              <p><strong>Contact:</strong> {profile.contact}</p>
            </>
          )}

          {profile.role === 'patient' && (
            <>
              <p><strong>Age:</strong> {profile.age}</p>
              <p><strong>Medical History:</strong> {profile.medicalHistory}</p>
            </>
          )}

          {profile.role === 'pharmacist' && (
            <>
              <p><strong>Pharmacy Name:</strong> {profile.pharmacyName}</p>
              <p><strong>Location:</strong> {profile.location}</p>
              <p><strong>Available Medicines:</strong> {profile.medicines?.join(', ')}</p>
            </>
          )}

          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <input
            name="name"
            placeholder="Name"
            value={form.name || ''}
            onChange={handleChange}
            required
          />
          <input
            name="email"
            placeholder="Email"
            value={form.email || ''}
            onChange={handleChange}
            required
            type="email"
          />

          {profile.role === 'doctor' && (
            <>
              <input
                name="specialization"
                placeholder="Specialization"
                value={form.specialization || ''}
                onChange={handleChange}
              />
              <input
                name="experience"
                type="number"
                placeholder="Experience"
                value={form.experience || ''}
                onChange={handleChange}
              />
              <input
                name="location"
                placeholder="Location"
                value={form.location || ''}
                onChange={handleChange}
              />
              <input
                name="contact"
                placeholder="Contact"
                value={form.contact || ''}
                onChange={handleChange}
              />
            </>
          )}

          {profile.role === 'patient' && (
            <>
              <input
                name="age"
                type="number"
                placeholder="Age"
                value={form.age || ''}
                onChange={handleChange}
              />
              <textarea
                name="medicalHistory"
                placeholder="Medical History"
                value={form.medicalHistory || ''}
                onChange={handleChange}
              />
            </>
          )}

          {profile.role === 'pharmacist' && (
            <>
              <input
                name="pharmacyName"
                placeholder="Pharmacy Name"
                value={form.pharmacyName || ''}
                onChange={handleChange}
              />
              <input
                name="location"
                placeholder="Location"
                value={form.location || ''}
                onChange={handleChange}
              />
              <textarea
                name="medicines"
                placeholder="Available Medicines (comma separated)"
                value={form.medicines?.join(', ') || ''}
                onChange={e => setForm({ ...form, medicines: e.target.value.split(',').map(m => m.trim()) })}
              />
            </>
          )}

          <input type="file" accept="image/*" onChange={handleImageChange} />
          <button type="submit">Save Changes</button>
          <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
          {msg && <p style={{ color: msg.includes('success') ? 'green' : 'red' }}>{msg}</p>}
        </form>
      )}
    </div>
  );
}
