import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Profile() {
  const [doctor, setDoctor] = useState(null);
  const [form, setForm] = useState({});
  const [msg, setMsg] = useState('');

  const token = localStorage.getItem('token'); // JWT from login

  // Fetch own doctor profile
  const fetchDoctor = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/doctors/me', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDoctor(res.data);
      setForm(res.data);
    } catch (err) {
      setMsg(err.response?.data?.message || 'Failed to load profile');
    }
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put('http://localhost:5000/api/doctors/me', form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDoctor(res.data.doctor);
      setMsg('Profile updated successfully');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Update failed');
    }
  };

  useEffect(() => {
    fetchDoctor();
  }, []);

  if (!doctor) return <p>Loading...</p>;

  return (
    <div>
      <h1>My Profile</h1>
      {msg && <p>{msg}</p>}
      <form onSubmit={handleSubmit}>
        <input name="name" value={form.name || ''} onChange={handleChange} placeholder="Name" />
        <input name="specialization" value={form.specialization || ''} onChange={handleChange} placeholder="Specialization" />
        <input name="experience" value={form.experience || ''} onChange={handleChange} placeholder="Experience" />
        <input name="availability" value={form.availability?.join(', ') || ''} onChange={handleChange} placeholder="Availability" />
        <input name="patientsServed" value={form.patientsServed || ''} onChange={handleChange} placeholder="Patients Served" />
        <input name="rating" value={form.rating || ''} onChange={handleChange} placeholder="Rating" />
        <input name="location" value={form.location || ''} onChange={handleChange} placeholder="Location" />
        <input name="contact" value={form.contact || ''} onChange={handleChange} placeholder="Contact" />
        <input name="image" value={form.image || ''} onChange={handleChange} placeholder="Image URL" />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
}
