import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function MyAppointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments/patient", {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch your appointments.");
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [user]);

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>You have no appointments yet.</p>;

  return (
    <div>
      <h2>My Appointments</h2>
      {appointments.map(app => (
        <div key={app._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
          <p><b>Doctor:</b> {app.doctorName}</p>
          <p><b>Email:</b> {app.doctorEmail}</p>
          <p><b>Date:</b> {app.date}</p>
          <p><b>Time:</b> {app.time}</p>
          <p><b>Status:</b> {app.status}</p>
          {app.meetLink && <p><b>Meeting Link:</b> <a href={app.meetLink} target="_blank">{app.meetLink}</a></p>}
        </div>
      ))}
    </div>
  );
}
