// frontend/src/pages/MyAppointments.jsx
import { useEffect, useState } from "react";
import { useAuth } from "../contexts/AuthContext";

export default function MyAppointments() {
  const { user, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      if (!token) return;

      try {
        const res = await fetch(
          "http://localhost:5000/api/appointments/patient",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        const data = await res.json();
        if (!res.ok) throw new Error(data.message || "Failed to fetch");
        setAppointments(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [token]);

  if (loading) return <p>Loading your appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>You have no appointments yet.</p>;

  return (
    <div style={{ maxWidth: "800px", margin: "auto" }}>
      <h2>My Appointments</h2>
      <table>
        <thead>
          <tr>
            <th>Doctor</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
            <th>Meeting Link</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((a) => (
            <tr key={a._id}>
              <td>{a.doctorName}</td>
              <td>{a.date}</td>
              <td>{a.time}</td>
              <td
                style={{
                  color:
                    a.status === "approved"
                      ? "green"
                      : a.status === "rejected"
                      ? "red"
                      : "gray",
                }}
              >
                {a.status}
              </td>
              <td>
                {a.status === "approved" && a.meetLink ? (
                  <a href={a.meetLink} target="_blank" rel="noopener noreferrer">
                    Join Meeting
                  </a>
                ) : (
                  "—"
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
