import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

export default function DoctorAppointments() {
  const { user } = useAuth(); // use hook instead of context
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [linkInputs, setLinkInputs] = useState({});

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`/api/appointments/doctor/${user.userId}`);
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleLinkChange = (appointmentId, value) => {
    setLinkInputs(prev => ({ ...prev, [appointmentId]: value }));
  };

  const handleUpdate = async (appointmentId, status) => {
    const meetLink = linkInputs[appointmentId] || '';
    try {
      await axios.put(`/api/appointments/${appointmentId}`, { status, meetLink });
      alert(`Appointment ${status}`);
      fetchAppointments();
    } catch (err) {
      console.error(err);
      alert('Failed to update');
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!appointments.length) return <p>No appointments yet.</p>;

  return (
    <div>
      <h2>Doctor Appointments</h2>
      <ul>
        {appointments.map(appt => (
          <li key={appt._id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
            <p><strong>Patient:</strong> {appt.patientName} ({appt.patientEmail})</p>
            <p><strong>Date:</strong> {appt.date} | <strong>Time:</strong> {appt.time}</p>
            <p><strong>Status:</strong> {appt.status}</p>
            {appt.status === 'pending' && (
              <>
                <input
                  type="text"
                  placeholder="Meeting link"
                  value={linkInputs[appt._id] || ''}
                  onChange={e => handleLinkChange(appt._id, e.target.value)}
                  style={{ marginRight: '10px' }}
                />
                <button onClick={() => handleUpdate(appt._id, 'approved')}>Approve</button>
                <button onClick={() => handleUpdate(appt._id, 'rejected')} style={{ marginLeft: '5px' }}>Reject</button>
              </>
            )}
            {appt.status === 'approved' && appt.meetLink && (
              <p><strong>Meeting Link:</strong> <a href={appt.meetLink} target="_blank" rel="noopener noreferrer">{appt.meetLink}</a></p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
