import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function DoctorDetail() {
  const { id } = useParams(); // doctor ID from the route
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchDoctor = async () => {
      if (!id) {
        setError('Invalid doctor ID');
        setLoading(false);
        return;
      }

      if (!token) {
        setError('You are not logged in');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDoctor(res.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Doctor not found');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id, token]);

  if (loading) return <p>Loading doctor details...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!doctor) return <p>No doctor found</p>;

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <h1>{doctor.name}</h1>
      <p><strong>Specialization:</strong> {doctor.specialization || 'N/A'}</p>
      <p><strong>Experience:</strong> {doctor.experience ? `${doctor.experience} years` : 'N/A'}</p>
      <p><strong>Availability:</strong> {doctor.availability?.length ? doctor.availability.join(', ') : 'N/A'}</p>
      <p><strong>Patients Served:</strong> {doctor.patientsServed || 0}</p>
      <p><strong>Rating:</strong> {doctor.rating || 'N/A'}</p>
      <p><strong>Location:</strong> {doctor.location || 'N/A'}</p>
      <p><strong>Contact:</strong> {doctor.contact || 'N/A'}</p>
      {doctor.image && (
        <img
          src={doctor.image.startsWith('/uploads') ? doctor.image : doctor.image}
          alt={doctor.name}
          style={{ width: '150px', borderRadius: '10px', marginTop: '10px' }}
        />
      )}
    </div>
  );
}
