// frontend/src/pages/DoctorAppointments.jsx
import { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";

export default function DoctorAppointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    const fetchAppointments = async () => {
      if (!user || !user.token) return;

      try {
        const res = await fetch("http://localhost:5000/api/appointments/doctor", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        });
        if (!res.ok) throw new Error("Failed to fetch appointments");
        const data = await res.json();
        setAppointments(data);
      } catch (err) {
        console.error(err);
        alert("Error fetching appointments");
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [user]);

  const handleDecision = async (appointmentId, decision) => {
    if (!user || !user.token) return;
    setActionLoading(true);

    try {
      const res = await fetch(`http://localhost:5000/api/appointments/${appointmentId}/decision`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({ decision }), // decision: "approved" | "rejected"
      });

      if (!res.ok) throw new Error("Failed to update appointment");

      const updatedAppointment = await res.json();

      // Update state
      setAppointments((prev) =>
        prev.map((app) => (app._id === updatedAppointment._id ? updatedAppointment : app))
      );
    } catch (err) {
      console.error(err);
      alert("Error updating appointment");
    } finally {
      setActionLoading(false);
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (!appointments.length) return <p>No appointment requests yet.</p>;

  return (
    <div>
      <h1>Appointment Requests</h1>
      <ul>
        {appointments.map((app) => (
          <li key={app._id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
            <p>
              <strong>Patient:</strong> {app.patientName} <br />
              <strong>Date:</strong> {app.date} <br />
              <strong>Time:</strong> {app.time} <br />
              <strong>Status:</strong> {app.status.toUpperCase()} <br />
              {app.status === "approved" && app.meetLink && (
                <>
                  <strong>Meeting Link:</strong>{" "}
                  <a href={app.meetLink} target="_blank" rel="noopener noreferrer">
                    Join Meeting
                  </a>
                </>
              )}
            </p>

            {/* Only show Accept/Reject for pending */}
            {app.status === "pending" && (
              <div>
                <button
                  onClick={() => handleDecision(app._id, "approved")}
                  disabled={actionLoading}
                  style={{ marginRight: "10px" }}
                >
                  Accept
                </button>
                <button onClick={() => handleDecision(app._id, "rejected")} disabled={actionLoading}>
                  Reject
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
