import React, { useState, useEffect } from "react";
import axios from "axios";

const DoctorProfile = ({ doctorId, token }) => {
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${doctorId}`);
        setDoctor(res.data);
        setFormData(res.data);
      } catch (err) {
        alert("Failed to load doctor");
      }
    };
    fetchDoctor();
  }, [doctorId]);

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.put(
        `http://localhost:5000/api/doctors/${doctorId}`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert(res.data.message);
      setDoctor(res.data.doctor);
    } catch (err) {
      alert(err.response?.data?.error || "Update failed");
    } finally {
      setLoading(false);
    }
  };

  if (!doctor) return <p>Loading...</p>;

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleChange} />
      <input name="specialization" value={formData.specialization} onChange={handleChange} />
      <input name="experience" type="number" value={formData.experience} onChange={handleChange} />
      <input name="availability" value={formData.availability.join(', ')} onChange={handleChange} />
      <input name="patientsServed" type="number" value={formData.patientsServed} onChange={handleChange} />
      <input name="rating" type="number" step="0.1" value={formData.rating} onChange={handleChange} />
      <input name="location" value={formData.location} onChange={handleChange} />
      <input name="contact" value={formData.contact} onChange={handleChange} />
      <input name="image" value={formData.image} onChange={handleChange} />
      <button type="submit" disabled={loading}>{loading ? "Updating..." : "Update Profile"}</button>
    </form>
  );
};

export default DoctorProfile;
