import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function DoctorAppointments() {
  const { user, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments/doctor", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError(err?.response?.data?.message || "Failed to load appointments");
      } finally {
        setLoading(false);
      }
    };
    if (user?.role === "doctor") fetchAppointments();
  }, [user, token]);

  const handleStatusChange = async (id, status) => {
    try {
      const res = await axios.patch(
        `http://localhost:5000/api/appointments/${id}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments((prev) =>
        prev.map((appt) => (appt._id === id ? res.data.appointment : appt))
      );
    } catch (err) {
      console.error(err);
      alert(err?.response?.data?.message || "Failed to update status");
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointment requests yet.</p>;

  return (
    <div>
      <h2>Appointment Requests</h2>
      {appointments.map((appt) => (
        <div key={appt._id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
          <p>
            <strong>Patient:</strong> {appt.patientName} ({appt.patientEmail})
          </p>
          <p>
            <strong>Date:</strong> {appt.date} <strong>Time:</strong> {appt.time}
          </p>
          <p>
            <strong>Status:</strong> {appt.status}
          </p>
          {appt.status === "pending" && (
            <>
              <button onClick={() => handleStatusChange(appt._id, "approved")}>Approve</button>
              <button onClick={() => handleStatusChange(appt._id, "rejected")}>Reject</button>
            </>
          )}
          {appt.meetLink && (
            <p>
              <strong>Meeting Link:</strong>{" "}
              <a href={appt.meetLink} target="_blank" rel="noopener noreferrer">
                {appt.meetLink}
              </a>
            </p>
          )}
        </div>
      ))}
    </div>
  );
}
