import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function DoctorDetail() {
  const { id } = useParams();
  const [doctor, setDoctor] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/doctors/${id}`)
      .then(res => setDoctor(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!doctor) return <p>Loading...</p>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-2">{doctor.name}</h1>
      <p>Specialization: {doctor.specialization}</p>
      <p>Experience: {doctor.experience} yrs</p>
      <p>Patients Served: {doctor.patientsServed}</p>
      <p>Rating: {doctor.rating}</p>
      <p>Availability: {doctor.availability.join(', ')}</p>
      <p>Location: {doctor.location}</p>
      <p>Contact: {doctor.contact}</p>
    </div>
  );
}
