// frontend/src/contexts/AuthContext.jsx

import { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const storedToken = localStorage.getItem("token");

    if (storedUser && storedToken) {
      setUser(JSON.parse(storedUser));
      setToken(storedToken);
    }

    setLoading(false);
  }, []);

  const login = async (email, password) => {
    if (!email || !password) throw new Error("Email and password are required");

    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      });

      const { user: userData, token } = res.data;

      if (!userData || !token) throw new Error("Invalid login response from server");

      localStorage.setItem("user", JSON.stringify(userData));
      localStorage.setItem("token", token);
      setUser(userData);
      setToken(token);

      return userData;
    } catch (err) {
      const message =
        err.response?.data?.message || err.message || "Login failed due to server error";
      console.error("Login error:", message);
      throw new Error(message);
    }
  };

  const signup = async (
    name,
    email,
    password,
    role,
    userId = "",
    specialization = "",
    experience = "",
    contact = ""
  ) => {
    if (!name || !email || !password || !role) throw new Error("All fields are required");

    try {
      const res = await axios.post("http://localhost:5000/api/auth/signup", {
        name,
        email,
        password,
        role,
        userId,
        specialization,
        experience,
        contact,
      });

      const { user: userData, token } = res.data;

      if (!userData || !token) throw new Error("Invalid signup response from server");

      localStorage.setItem("user", JSON.stringify(userData));
      localStorage.setItem("token", token);
      setUser(userData);
      setToken(token);

      return userData;
    } catch (err) {
      const message =
        err.response?.data?.message || err.message || "Signup failed due to server error";
      console.error("Signup error:", message);
      throw new Error(message);
    }
  };

  const fetchCurrentUser = async () => {
    if (!token) return null;
    try {
      const res = await axios.get("http://localhost:5000/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(res.data);
      return res.data;
    } catch (err) {
      console.error("Fetch current user error:", err.response?.data || err.message);
      logout(); // token invalid or expired
      return null;
    }
  };

  const logout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setToken(null);
  };

  return (
    <AuthContext.Provider
      value={{ user, token, loading, login, signup, logout, fetchCurrentUser, setUser }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
