import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

export default function BookAppointment() {
  const { user } = useAuth();
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      const res = await axios.get('/api/doctors');
      setDoctors(res.data.filter(doc => doc.userId !== user.userId));
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedDoctor || !date || !time) return alert('Fill all fields');

    const doctor = doctors.find(d => d.userId === selectedDoctor);

    try {
      await axios.post('/api/appointments', {
        patientId: user.userId,
        patientName: user.name,
        patientEmail: user.email,
        doctorId: doctor.userId,
        doctorName: doctor.name,
        doctorEmail: doctor.email,
        date,
        time,
        status: 'pending'
      });
      alert('Appointment requested');
      setSelectedDoctor('');
      setDate('');
      setTime('');
    } catch (err) {
      console.error(err);
      alert('Booking failed');
    }
  };

  return (
    <div>
      <h2>Book Appointment</h2>
      <form onSubmit={handleSubmit}>
        <select value={selectedDoctor} onChange={e => setSelectedDoctor(e.target.value)}>
          <option value="">Select Doctor</option>
          {doctors.map(doc => (
            <option key={doc.userId} value={doc.userId}>{doc.name} ({doc.email})</option>
          ))}
        </select>
        <br />
        <input type="date" value={date} onChange={e => setDate(e.target.value)} />
        <input type="time" value={time} onChange={e => setTime(e.target.value)} />
        <br />
        <button type="submit">Request Appointment</button>
      </form>
    </div>
  );
}
