// frontend/src/components/PharmacistCard.jsx
import React from "react";
import { Link } from "react-router-dom";
import "./PharmacistCard.css";

export default function PharmacistCard({ pharmacist, highlight }) {
  return (
    <div className="pharmacist-card">
      <img
        src={
          pharmacist.image
            ? `http://localhost:5000${pharmacist.image}`
            : "/default-avatar.png"
        }
        alt={pharmacist.name}
        className="pharmacist-image"
      />

      <h3>{pharmacist.name}</h3>
      <p>
        <strong>Pharmacy:</strong> {pharmacist.pharmacyName}
      </p>
      <p>
        <strong>Location:</strong> {pharmacist.location || "N/A"}
      </p>

      {/* âœ… Show medicines if available */}
      {pharmacist.medicines && pharmacist.medicines.length > 0 && (
        <div className="medicine-list">
          <strong>Medicines:</strong>
          <ul>
            {pharmacist.medicines.map((med, i) => {
              if (highlight) {
                const regex = new RegExp(`(${highlight})`, "gi");
                const parts = med.split(regex);

                return (
                  <li key={i}>
                    {parts.map((part, idx) =>
                      regex.test(part) ? (
                        <mark key={idx}>{part}</mark>
                      ) : (
                        part
                      )
                    )}
                  </li>
                );
              }
              return <li key={i}>{med}</li>;
            })}
          </ul>
        </div>
      )}

      <Link
        to={`/pharmacists/${pharmacist.userId}`}
        className="view-profile-link"
      >
        View Profile
      </Link>
    </div>
  );
}
