import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/doctors/me', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDoctor(res.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load profile');
      setDoctor(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) fetchProfile();
    else setError('You are not logged in');
  }, [token]);

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      <h1>My Profile</h1>
      <img
        src={doctor.image}
        alt={doctor.name}
        style={{ width: '150px', borderRadius: '50%' }}
      />
      <p><strong>Name:</strong> {doctor.name}</p>
      <p><strong>Specialization:</strong> {doctor.specialization}</p>
      <p><strong>Experience:</strong> {doctor.experience} years</p>
      <p><strong>Availability:</strong> {doctor.availability.join(', ')}</p>
      <p><strong>Patients Served:</strong> {doctor.patientsServed}</p>
      <p><strong>Rating:</strong> {doctor.rating}</p>
      <p><strong>Location:</strong> {doctor.location}</p>
      <p><strong>Contact:</strong> {doctor.contact}</p>
    </div>
  );
}
