import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import PharmacistCard from "../components/PharmacistCard";
import "./MedicineSearch.css";

export default function MedicineSearch() {
  const [queryParams] = useSearchParams();
  const [query, setQuery] = useState(queryParams.get("name") || "");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Example quick search options
  const quickOptions = [
    "Paracetamol",
    "Ibuprofen",
    "Amoxicillin",
    "Cetirizine",
    "Azithromycin",
  ];

  const handleSearch = async (e) => {
    if (e) e.preventDefault();
    if (!query.trim()) {
      setError("Please enter a medicine name.");
      return;
    }

    setError("");
    setLoading(true);

    try {
      const res = await axios.get(
        `http://localhost:5000/api/pharmacists/search/medicine?name=${encodeURIComponent(
          query
        )}`
      );
      setResults(res.data);
    } catch (err) {
      console.error("Error searching medicine:", err);
      setError("Failed to fetch results.");
    } finally {
      setLoading(false);
    }
  };

  // Run search when query param exists
  useEffect(() => {
    if (query) handleSearch();
    // eslint-disable-next-line
  }, [query]);

  // ‚úÖ Handle quick option click
  const handleQuickSearch = (medicine) => {
    setQuery(medicine);
    setTimeout(() => handleSearch(), 0); // run after state update
  };

  return (
    <div className="medicine-search-container">
      <h2>üîç Search Medicine Availability</h2>

      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter medicine name (e.g., Paracetamol)"
          className="search-input"
        />
        <button type="submit" className="search-btn">
          Search
        </button>
      </form>

      {/* ‚úÖ Quick search buttons */}
      <div className="quick-search-options">
        <p>Quick search:</p>
        {quickOptions.map((med) => (
          <button
            key={med}
            onClick={() => handleQuickSearch(med)}
            className="quick-btn"
          >
            {med}
          </button>
        ))}
      </div>

      {loading && <p>Searching...</p>}
      {error && <p className="error">{error}</p>}

      <div className="pharmacist-results">
        {results.length > 0 ? (
          results.map((pharmacist) => (
            <PharmacistCard
              key={pharmacist.userId}
              pharmacist={pharmacist}
              highlight={query} // ‚úÖ highlight searched med
            />
          ))
        ) : (
          !loading &&
          query && <p>No pharmacists found stocking "{query}".</p>
        )}
      </div>
    </div>
  );
}
