import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './PharmacistCard.css';

export default function PharmacistCard({ userId }) {
  const defaultImage = 'http://localhost:5000/uploads/Pharmacist.jpg';
  const [pharmacist, setPharmacist] = useState(null);
  const [imgSrc, setImgSrc] = useState(defaultImage);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPharmacist = async () => {
      try {
        // Fetch JSON data from dummyPharmacistData.json
        const res = await fetch('http://localhost:5000/data/dummyPharmacistData.json');
        const data = await res.json();

        // Find pharmacist by userId
        const found = data.find((p) => p.userId === userId);
        if (found) {
          setPharmacist(found);

          // Set image
          const url = found.image
            ? `http://localhost:5000${found.image.startsWith('/') ? found.image : '/uploads/' + found.image}`
            : defaultImage;

          // Preload image
          const img = new Image();
          img.src = url;
          img.onload = () => setImgSrc(url);
          img.onerror = () => setImgSrc(defaultImage);
        }
      } catch (err) {
        console.error('Error loading pharmacist data:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchPharmacist();
  }, [userId]);

  if (loading) return <p>Loading pharmacist...</p>;
  if (!pharmacist) return <p>Pharmacist not found</p>;

  return (
    <div className="pharmacist-card">
      <img
        src={imgSrc}
        alt={pharmacist.name || 'Pharmacist'}
        className="pharmacist-image"
        onError={(e) => (e.target.src = defaultImage)}
      />
      <h3>{pharmacist.name || 'N/A'}</h3>
      <p><strong>Pharmacy:</strong> {pharmacist.pharmacyName || 'N/A'}</p>
      <p><strong>Location:</strong> {pharmacist.location || 'N/A'}</p>
      <Link to={`/pharmacists/${pharmacist.userId}`} className="view-profile-link">
        View Profile
      </Link>
    </div>
  );
}
