import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './DoctorCard.css';

export default function DoctorCard({ doctor }) {
  // Determine initial image URL
  const getImageUrl = () => {
    if (!doctor.image) return 'http://localhost:5000/uploads/default-doctor.png';
    return doctor.image.startsWith('http')
      ? doctor.image
      : `http://localhost:5000${doctor.image.startsWith('/') ? doctor.image : '/' + doctor.image}`;
  };

  const [imgSrc, setImgSrc] = useState(getImageUrl());

  // Prevent flicker by updating src only if broken
  const handleImageError = () => {
    if (imgSrc !== 'http://localhost:5000/uploads/default-doctor.png') {
      setImgSrc('http://localhost:5000/uploads/default-doctor.png');
    }
  };

  // Update image if doctor.image changes
  useEffect(() => {
    setImgSrc(getImageUrl());
  }, [doctor.image]);

  return (
    <div className="doctor-card">
      <img
        src={imgSrc}
        alt={doctor.name || 'Doctor'}
        className="doctor-card-image"
        onError={handleImageError}
      />
      <div className="doctor-card-info">
        <h2 className="doctor-card-name">{doctor.name}</h2>
        {doctor.specialization && (
          <p className="doctor-card-specialization">
            Specialization: {doctor.specialization}
          </p>
        )}
        {doctor.experience !== undefined && (
          <p className="doctor-card-experience">Experience: {doctor.experience} yrs</p>
        )}
        {doctor.rating !== undefined && (
          <p className="doctor-card-rating">Rating: {doctor.rating}</p>
        )}
        <Link to={`/doctors/${doctor.userId}`} className="doctor-card-link">
          View Details
        </Link>
      </div>
    </div>
  );
}
