// frontend/src/pages/Profile.jsx
import React, { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import axios from "axios";
import './Profile.css';

export default function Profile() {
  const { user, setUser } = useAuth();
  const [doctorData, setDoctorData] = useState(null);
  const [formData, setFormData] = useState({});
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  // Fetch doctor data if user is doctor
  useEffect(() => {
    const fetchDoctor = async () => {
      if (!user || !user._id) return;

      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${user._id}`);
        setDoctorData(res.data);

        setFormData({
          name: res.data.name || "",
          email: user.email || "",
          userId: user.userId || "",
          specialization: res.data.specialization || "",
          experience: res.data.experience || "",
          patientsServed: res.data.patientsServed || "",
          rating: res.data.rating || "",
          location: res.data.location || "",
          contact: res.data.contact || "",
          availability: res.data.availability?.join(", ") || "",
        });
      } catch (err) {
        console.error(err);
        setMsg("Failed to load doctor profile");
      }
    };

    fetchDoctor();
  }, [user]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => setProfilePhoto(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) return;
    setLoading(true);

    try {
      const data = new FormData();
      Object.keys(formData).forEach((key) => data.append(key, formData[key]));
      if (profilePhoto) data.append("profilePhoto", profilePhoto);

      const res = await axios.put(
        `http://localhost:5000/api/users/${user._id}`,
        data,
        { headers: { Authorization: `Bearer ${user.token}` } }
      );

      setUser({ ...res.data.user, token: user.token });
      localStorage.setItem("user", JSON.stringify({ ...res.data.user, token: user.token }));
      setMsg("Profile updated successfully!");
    } catch (err) {
      console.error(err);
      setMsg(err.response?.data?.message || "Update failed");
    } finally {
      setLoading(false);
    }
  };

  const handleDeletePhoto = async () => {
    if (!user) return;

    try {
      await axios.delete(`http://localhost:5000/api/users/profile-photo/${user._id}`, {
        headers: { Authorization: `Bearer ${user.token}` },
      });

      setProfilePhoto(null);
      setUser({ ...user, profilePhoto: null });
      localStorage.setItem("user", JSON.stringify({ ...user, profilePhoto: null }));
      setMsg("Profile photo deleted");
    } catch (err) {
      console.error(err);
      setMsg(err.response?.data?.message || "Delete failed");
    }
  };

  if (!user || !doctorData) return <p>Loading profile...</p>;

  return (
    <div className="profile-page">
      <h2>My Profile</h2>
      {msg && <p className="msg">{msg}</p>}
      <form onSubmit={handleSubmit} className="profile-form">
        <div>
          <label>Name</label>
          <input name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>UserId</label>
          <input name="userId" value={formData.userId} onChange={handleChange} />
        </div>

        {user.role === "doctor" && (
          <>
            <div>
              <label>Specialization</label>
              <input name="specialization" value={formData.specialization} onChange={handleChange} />
            </div>
            <div>
              <label>Experience (years)</label>
              <input type="number" name="experience" value={formData.experience} onChange={handleChange} />
            </div>
            <div>
              <label>Patients Served</label>
              <input type="number" name="patientsServed" value={formData.patientsServed} onChange={handleChange} />
            </div>
            <div>
              <label>Rating</label>
              <input type="number" step="0.1" name="rating" value={formData.rating} onChange={handleChange} />
            </div>
            <div>
              <label>Location</label>
              <input name="location" value={formData.location} onChange={handleChange} />
            </div>
            <div>
              <label>Contact</label>
              <input name="contact" value={formData.contact} onChange={handleChange} />
            </div>
            <div>
              <label>Availability (comma separated)</label>
              <input name="availability" value={formData.availability} onChange={handleChange} />
            </div>
          </>
        )}

        <div>
          <label>Profile Photo</label>
          <input type="file" onChange={handleFileChange} />
          {user.profilePhoto && (
            <div>
              <img src={`http://localhost:5000/uploads/${user.profilePhoto}`} alt="Profile" width="100" />
              <button type="button" onClick={handleDeletePhoto}>Delete Photo</button>
            </div>
          )}
        </div>

        <button type="submit" disabled={loading}>{loading ? "Updating..." : "Update Profile"}</button>
      </form>
    </div>
  );
}
