// frontend/src/pages/BookAppointment.jsx
import { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./BookAppointment.css";

export default function BookAppointment() {
  const { doctorId } = useParams();
  const { user: patient, token } = useAuth(); // logged-in patient
  const navigate = useNavigate();

  const [doctor, setDoctor] = useState(null);
  const [loadingDoctor, setLoadingDoctor] = useState(true);
  const [error, setError] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const [successMsg, setSuccessMsg] = useState("");

  // Fetch doctor details
  useEffect(() => {
    if (!doctorId) return setError("No doctor selected.");

    const fetchDoctor = async () => {
      try {
        setLoadingDoctor(true);
        const res = await axios.get(`http://localhost:5000/api/doctors/${doctorId}`);
        setDoctor(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load doctor details.");
      } finally {
        setLoadingDoctor(false);
      }
    };

    fetchDoctor();
  }, [doctorId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!date || !time) return setError("Please select date and time.");
    if (!doctor) return setError("No doctor selected.");
    if (!patient) return setError("Patient not logged in.");

    const appointmentData = {
      doctorId: doctor.userId,
      doctorName: doctor.name,
      doctorEmail: doctor.email,
      patientId: patient.userId,
      patientName: patient.name,
      patientEmail: patient.email,
      date,
      time,
    };

    try {
      setSubmitting(true);
      setError("");
      const res = await axios.post(
        "http://localhost:5000/api/appointments",
        appointmentData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccessMsg("Appointment request sent successfully!");
      setTimeout(() => navigate("/my-appointments"), 1500);
    } catch (err) {
      console.error(err);
      setError(
        err?.response?.data?.message || "Failed to send appointment request."
      );
    } finally {
      setSubmitting(false);
    }
  };

  if (loadingDoctor) return <p>Loading doctor details...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!doctor) return <p>No doctor selected.</p>;

  return (
    <div className="book-appointment-page">
      <Link to={`/doctors/${doctor.userId}`}>‚Üê Back to Doctor Details</Link>
      <h1>Book Appointment with {doctor.name}</h1>

      <div className="doctor-info">
        <p><strong>Specialization:</strong> {doctor.specialization || "N/A"}</p>
        <p><strong>Experience:</strong> {doctor.experience || 0} years</p>
        <p><strong>Rating:</strong> {doctor.rating || "N/A"}</p>
      </div>

      <form onSubmit={handleSubmit} className="appointment-form">
        <h2>Patient Info</h2>
        <p><strong>Name:</strong> {patient.name}</p>
        <p><strong>Email:</strong> {patient.email}</p>

        <h2>Appointment Details</h2>
        <label>
          Date:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </label>

        <label>
          Time:
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </label>

        <button type="submit" disabled={submitting}>
          {submitting ? "Sending..." : "Book Appointment"}
        </button>

        {successMsg && <p style={{ color: "green" }}>{successMsg}</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}
      </form>
    </div>
  );
}
